mapper.delete:

delete from sys_file
where 

id = #{id,jdbcType=VARCHAR}


file_md5 = #{fileMd5,jdbcType=VARCHAR}

mapper.select:

select id, file_name, saved_name, file_size, file_path,file_md5,file_progress,upload_time,create_by, create_date, update_by, 
update_date, del_flag
from sys_file
where 1=1 

 and id = #{id,jdbcType=VARCHAR} 


  and file_md5  = #{fileMd5}
