mapper.select:
	 	 SELECT 

	 	  FROM `t_device_info` t











	 	   		WHERE t.del_flag = '0'


          AND t.device_sn like #{deviceSnLike}  


          AND t.device_type = #{deviceType}


          	  AND t.manufacturer_no = #{manufacturerNo}


          	  and t.organ_id IN (SELECT id FROM sys_office WHERE parent_ids LIKE
					'%,'||#{organId}||',%'
					'%,'+#{organId}+',%'
					CONCAT('%,', #{organId}, ',%')
					AND del_flag = '0'
					    UNION select id from sys_office where id = #{organId}
				    AND del_flag = '0')


          	  AND t.mer_id = #{merId}


          	  AND t.shop_id = #{shopId}


          order by t.id

SELECT device_os_version, client_identification, count(*) AS device_count FROM `t_device_info` t WHERE t.del_flag = '0'
AND t.device_os_version = #{deviceOsVersion}
GROUP BY device_os_version ORDER BY device_os_version DESC

SELECT t.id, mi.manufacturer_no , mi.manufacturer_name AS manufacturerName, t.device_sn, t.device_type, t.device_os_version, t.device_bank, t.device_info, t.device_status, t.os_status, t.os_upgrade_type, t.os_msg, t.hardware_version, t.mer_id AS merId, t.shop_id, t.apply_date, t.install_location, t.install_date, t.device_sort, t.mac_no, t.scope_mode, t.labels, m.mer_name AS merName, t.location, t.organ_id, s.store_name AS shopName FROM `t_device_info` t
WHERE t.del_flag = '0'
AND t.device_sn = #{deviceSn}
AND t.device_type = #{deviceType}
AND t.manufacturer_no = #{manufacturerNo}
AND t.id = #{id}

SELECT t.id, t.device_sn, t.device_type, sa.customer_name AS client_no, t.device_bank, mi.manufacturer_name AS manufacturer_no, t.device_info, t.device_status, t.os_status, t.os_upgrade_type, t.device_os_version, t.os_msg FROM `t_device_info` t
WHERE t.del_flag = '0' AND t.`device_sn` IN (SELECT ad.device_sn FROM t_app_device ad WHERE ad.`apk_id` = #{id})
AND
(t.manufacturer_no = #{appDeviceType.manuNo} AND t.device_type = #{appDeviceType.deviceType})
AND t.manufacturer_no = #{manufacturerNo}
AND t.device_type = #{deviceType}
AND t.client_no in (select customer_id from sass_customer where customer_id_path like CONCAT('%/', #{clientNo}, '%'))
AND t.device_sn = #{deviceSn}

SELECT t.id, t.device_sn, t.device_type, t.client_no, t.device_bank, t.manufacturer_no, t.device_info, t.device_status, t.os_status, t.os_upgrade_type, t.device_os_version, t.os_msg FROM t_device_info t WHERE t.del_flag = '0' AND t.`device_sn` NOT IN (SELECT ad.device_sn FROM t_app_device ad WHERE ad.`apk_id` = #{id})
AND (FIND_IN_SET(t.device_sn,#{deviceSnStr}))
AND
(t.manufacturer_no = #{appDeviceType.manuNo} AND t.device_type = #{appDeviceType.deviceType})
AND t.client_no in (select customer_id from sass_customer where customer_id_path like CONCAT('%/', #{clientNo}, '%'))
AND t.manufacturer_no = #{manufacturerNo}
AND t.device_type = #{deviceType}
AND t.device_sn = #{deviceSn}

SELECT * FROM ( SELECT t.id, t.device_sn, t.device_type, sa.customer_name AS client_no, t.device_bank, t.device_os_version, o.os_version AS os_version, mi.manufacturer_name AS manufacturer_no, t.device_status, t.os_status, t.os_upgrade_type, t.os_msg, STRCMP(t.os_version_shifter ,rmd.os_version_shifter) AS version_compare_value FROM `t_device_info` t
WHERE t.del_flag = '0'
AND t.device_sn like #{deviceSnLike}
AND device_type = #{deviceType}
AND t.manufacturer_no = #{manufacturerNo}
AND EXISTS (SELECT 1 FROM `t_rom_device` rd WHERE rd.`record_rom_id` = #{id} AND rd.`device_id` = t.id) ) a WHERE 1 = 1
AND a.version_compare_value >= 0
AND ( a.version_compare_value < 0 OR a.version_compare_value is null)

SELECT t.id, t.device_sn, t.device_type, o.app_name, o.app_version, o.app_package, rmd.upgrade_type, sdi.label AS upgrade_desc, mi.manufacturer_name AS manufacturer_no, t.device_status, t.app_info FROM `t_device_info` t
LEFT JOIN t_app_device rmd ON rmd.`device_sn` = t.device_sn AND rmd.`app_record_id` = #{id} LEFT JOIN t_app_version o ON o.id = rmd.apk_id LEFT JOIN sys_dict sdi ON sdi.value = rmd.upgrade_desc AND sdi.type = 'app_upflag'
WHERE t.del_flag = '0'
AND t.device_sn = #{deviceSn}
AND t.manufacturer_no = #{manufacturerNo}
AND t.device_type = #{deviceType}
AND EXISTS(SELECT 1 FROM `t_app_device` WHERE `app_record_id` = #{id} AND `device_sn` = t.device_sn)

SELECT COUNT(*) FROM `t_device_info` t where t.del_flag = '0'
AND t.organ_id IN (SELECT id FROM sys_office WHERE parent_ids LIKE '%,'||#{organId}||',%' '%,'+#{organId}+',%' CONCAT('%,', #{organId}, ',%') AND del_flag = '0' UNION select id from sys_office where id = #{organId} AND del_flag = '0')
AND t.update_date >= #{beginDateStr}
AND t.update_date <= #{endDateStr}

SELECT COUNT(*) FROM `t_device_info` t where t.del_flag = '0'
AND t.update_date >= #{beginDateStr}
AND t.update_date <= #{endDateStr}
AND t.client_no in (select customer_id from sass_customer where customer_id_path like CONCAT('%/', #{clientNo}, '%'))

mapper.update:
update t_device_info set device_bund_state = '1'

	,mer_id = #{merId,jdbcType=VARCHAR}


	,shop_id = #{shopId,jdbcType=VARCHAR}

where id = #{id,jdbcType=VARCHAR}

mapper.select:
select t.id, t.device_sn, t.device_sort, t.device_type, t.location, t.organ_id, so.name organ_name, t.mer_id, m.mer_name, t.shop_id, s.store_name shop_name, t.device_bund_state from t_device_info t
join t_merchant m on t.mer_id = m.mer_id and m.id = #{mId,jdbcType=VARCHAR}
left join t_merchant m on t.mer_id = m.mer_id
join t_store s on t.shop_id = s.store_id and s.id = #{sId,jdbcType=VARCHAR}
left join t_store s on t.shop_id = s.store_id
left join sys_office so on t.organ_id = so.id
where 1=1
and t.organ_id = #{organId,jdbcType=VARCHAR}
and t.mer_id = #{merId,jdbcType=VARCHAR}
and t.shop_id = #{shopId,jdbcType=VARCHAR}
and t.device_sn = #{deviceSn,jdbcType=CHAR}
and t.device_bund_state = #{deviceBundState,jdbcType=CHAR}
