mapper.select:
SELECT id, app_name, app_package, app_version, app_file, app_md5, app_description, upgrade_mode FROM `t_app_version` WHERE del_flag = '0'
AND app_name = #{appName}
AND app_version = #{appVersion}
AND app_package = #{appPackage}
AND id <> #{id}

SELECT id, app_name, app_package, app_version, app_file, app_md5, app_description, upgrade_mode FROM `t_app_version` t WHERE 1 = 1
AND t.app_package = #{appPackage}
AND t.app_version in (SELECT MAX(app_version) FROM t_app_version WHERE app_package = #{appPackage})
